local trainTrackLength = 18129.582799971

local trainSpawnPoint = {
	x = 2840.87598, y = 1566.80945, z = 10.82031
}

--[[
	This table is used to find the train position quicker, by making a smaller summary of it. 
]]
local quickIndex = {}
--

--[[
	Train Speed
]]
local trainSpeed = 1


--[[
	How long it takes to cycle the map. This will be filled in later in the code.
]]
local duration = 0


--[[
	For making the train synced across all clients and server, there is a timing value used.
	Pros:
	- Easier to maintain
	- This will reduce network (only one message is needed, which will allow all other clients to do the same thing)
	
	Cons:
	- Flexibility (you can't stop a train, unless you stop them all)
]]

local startClientTiming = 0
local serverTiming = 0

addEvent("syncTrainsTiming",true)
addEventHandler("syncTrainsTiming",resourceRoot,
function (serverTiming_)
	
	startClientTiming = getTickCount()
	serverTiming = tonumber(serverTiming_)
end)


--[[
	All trains including data
	- Format: array
]]
local trainDataContainer = {}

--[[
	Configuration of the trains from serverside - used to create the trains
	- Format: array
]]
local trainServersideData

--[[
	The server gives the client everything he needs to run the script
]]
addEvent("onClientTrainResourceStart",true)
addEventHandler("onClientTrainResourceStart",resourceRoot,
function (amountOfTrains,trainSpeed_,trainServersideData_, serverTiming_)
	trainServersideData = trainServersideData_
	
	-- loading/calculating serverside settings --
	duration = trainTrackLength/(trainSpeed_/1000)
	trainSpeed = trainSpeed_
	startClientTiming = getTickCount()
	serverTiming = tonumber(serverTiming_)

	---------------------------------------------
	
	--[[
		Traintrack set-up + BEGIN and END position
	]]
	local beginPosX, beginPosY, beginPosZ = trainLocationTable[1][1], trainLocationTable[1][2], trainLocationTable[1][3]
	local endPosX,endPosY,endPosZ = trainLocationTable[#trainLocationTable][1],trainLocationTable[#trainLocationTable][2],trainLocationTable[#trainLocationTable][3]
	local distanceTrainTrack = 0--

	for i=1,#trainLocationTable do
		local data = trainLocationTable[i]
		local data2 = trainLocationTable[i+1]
		if data2 then
			local distanceBetweenPoints = getDistanceBetweenPoints3D(data[1],data[2],data[3],data2[1],data2[2],data2[3])
			distanceTrainTrack = distanceTrainTrack + distanceBetweenPoints

			data2[4] = distanceTrainTrack
		end
	end
	
	local distanceBetweenPoints = getDistanceBetweenPoints3D(beginPosX,beginPosY,beginPosZ,endPosX,endPosY,endPosZ)
	trainLocationTable[1][4] = distanceTrainTrack + distanceBetweenPoints
	
	
	for trainIndex=1,amountOfTrains do
		local trainStartLocation = (trainTrackLength/amountOfTrains)*trainIndex
		
		local trainTrackIndex = 1
		repeat
			trainTrackIndex = trainTrackIndex + 1
			local isNextPoint
			local trainTrackPointData = trainLocationTable[trainTrackIndex]
			if trainTrackIndex < #trainLocationTable + 1 then
				isNextPoint = trainStartLocation < trainTrackPointData[4]
				if isNextPoint then
					trainTrackIndex = trainTrackIndex - 1
				end
			else
				trainTrackIndex = 1
				isNextPoint = true
			end
		until isNextPoint
		
		
		local trainStartPoint = trainLocationTable[trainTrackIndex]
		local spawnX,spawnY,spawnZ = trainStartPoint[1],trainStartPoint[2],trainStartPoint[3]
		trainDataContainer[#trainDataContainer+1] = {["startLocation"]=trainStartLocation,["spawnLocation"]={spawnX,spawnY,spawnZ}}

	end
	
	addEventHandler("onClientPreRender", root, renderTrainPosition)
end)



local destroyTrain = function (trainIndex)
	local trainData = trainDataContainer[trainIndex]
	if trainData then
		
		-- train
		local previousTrain = trainData["train"]
		if previousTrain then 
			if isElement(previousTrain) then
				destroyElement(previousTrain)
			end
			trainData["train"] = nil
		end
		
		-- driver
		local previousDriver = trainData["driver"]
		if previousDriver then
			if isElement(previousDriver) then
				destroyElement(previousDriver)
			end
			trainData["driver"] = nil
		end
		
		-- carts
		local trainCarts = trainData["trainCarts"]
		if trainCarts then
			for i=1,#trainCarts do
				local trainCart = trainCarts[i]
				if isElement(trainCart) then
					destroyElement(trainCart)
				end
			end
			trainData["trainCarts"] = nil
		end
		
		return true
	end
end



local createTrain = function (trainIndex,spawnX,spawnY,spawnZ)		
	local trainServerData = trainServersideData[trainIndex]
	local trainData = trainDataContainer[trainIndex]
	if trainServerData and trainData then
		if trainData["train"] or trainData["driver"] or trainData["trainCarts"] then
			destroyTrain(trainIndex)
		end
		
		
		local train = createVehicle ( trainServerData["model"], trainSpawnPoint.x, trainSpawnPoint.y, trainSpawnPoint.z, 0, 0, 0 )
		
		if train then
			
			setElementPosition(train, spawnX,spawnY,spawnZ, false)

			
			setVehicleDamageProof(train,true)

			setTrainDirection ( train, true )
			
			
			
			local trainBlip = createBlipAttachedTo ( train, 0, 1, 100, 100, 100, 255, -100, 600)
			
			setElementParent(trainBlip,train)
			
			
			local driver = createPed(trainServerData["driver"],0,0,0)
			
			warpPedIntoVehicle ( driver, train )
			
			setVehicleOverrideLights ( train, 2 )
			
			trainData["train"] = train
			trainData["driver"] = driver
			

			local trainServerData = trainServersideData[trainIndex]
			local cartsData = trainServerData["cartsData"]
			
			local trainData = trainDataContainer[trainIndex]
			
			local spawnLocation = trainData["spawnLocation"]
			trainData["trainCarts"]= {}
			local trainCarts = trainData["trainCarts"]
			for i=1,#cartsData do
				local cartData = cartsData[i]
			
				local trainCart = createVehicle (cartData.model, trainSpawnPoint.x, trainSpawnPoint.y, trainSpawnPoint.z )
				if trainCart then
					
					
					setVehicleDamageProof(trainCart,true)
					
					setVehicleOverrideLights ( trainCart, 2 )
					
					local trainCartBlip = createBlipAttachedTo ( trainCart, 0, 1, 200, 200, 200, 255, -101, 170) 
					setElementParent(trainCartBlip, trainCart)
					

					if i == 1 then
						attachTrailerToVehicle ( train, trainCart )
						trainCarts[i] = trainCart
					else
						local attachTo = trainCarts[i-1]
						attachTrailerToVehicle ( attachTo, trainCart )
						trainCarts[i] = trainCart
					end
					local attachedData = cartData["attached"]
					if attachedData then
						for i=1,#attachedData do
							local elementData = attachedData[i] 
							local elementType = elementData["elementType"]
							
							local model = elementData.model
							
							
							local element
							if elementType == "object" or not elementType then
								element = createObject(model,0,0,10000)
								if element then
									local scale = elementData["scale"]
									if scale and scale ~= 1 then
										setObjectScale(element, scale)
									end
									setElementDoubleSided(element,true)
									
									setObjectBreakable(element, false)
								end
							elseif elementType == "vehicle" then
								
								local numberplate = elementData["plate"] or "NL"
								
								element = createVehicle(model,0,0,10000, 0,0,0, numberplate)
								
								if element then
									local color = elementData["color"] 
									if color then
										setVehicleColor(element, unpack(color))
									else
										setVehicleColor(element, 38,55,57,245,245,245,0,0,0,0,0,0)
									end
									setVehicleDamageProof(element,true)
									setVehicleOverrideLights ( element, 2 )
								end
							elseif elementType == "ped" then
								element = createPed(model, 0, 0, 10000)
								if element then
									local animBlock, animName = elementData["animBlock"], elementData["animName"]
									if animBlock and animName then
										setPedAnimation(element, animBlock, animName,  -1, true, false, false)
									end
								end
							elseif elementType == "marker" then
								element = createMarker(0,0,10000, elementData["type"] or "checkpoint", elementData["size"] or 3, elementData["r"] or 0,elementData["g"] or 0,elementData["b"] or 255,  elementData["a"] or 255)
							end
							
							if element then
								local alpha = elementData["alpha"]
								if alpha and alpha ~= 255 then
									setElementAlpha(element, alpha)
								end
								
								setElementData(element, "train-component", true)
								
								setElementParent(element,trainCart)
								setElementCollidableWith(element,trainCart,false)
								local offset = elementData["offset"]
								attachElements(element,trainCart,offset[1],offset[2],offset[3],offset[4],offset[5],offset[6])
							end
						end
						
					end
				else
					break -- Should not be able to execute.
				end
			end
			
			
			return train
		end
	end
end





local math_sqrt = math.sqrt
local getDistanceBetweenPointAndSegment3D = function (pointX, pointY, pointZ, x1, y1, z1, x2, y2, z2)
	-- vector1 --
	local A = pointX - x1 
	local B = pointY - y1
	local C = pointZ - z1
	--
	-- vector2 --
	local D = x2 - x1
	local E = y2 - y1
	local F = z2 - z1
	--
	local point = A * D + B * E + C * F
	local lenSquare = D * D + E * E + F * F
	local parameter = point / lenSquare
 
	local shortestX
	local shortestY
	local shortestZ
 
	if parameter < 0 then
		shortestX = x1
    	shortestY = y1
		shortestZ = z1
	elseif parameter > 1 then
		shortestX = x2
		shortestY = y2
		shortestZ = z2

	else
		shortestX = x1 + parameter * D
		shortestY = y1 + parameter * E
		shortestZ = z1 + parameter * F
	end

	local distance = math_sqrt(((shortestX-(pointX))^2+(shortestY-(pointY))^2+(shortestZ-(pointZ))^2))
 
	return distance
end



local nextTrainCorrectionCheck = 0 -- validate issues with the train...
function renderTrainPosition ()
	-- debug train path --
	--[[
	for i=1,#trainLocationTable do
		local data = trainLocationTable[i]
		local data2 = trainLocationTable[i+1]
		if data2 then
			dxDrawLine3D(data[1],data[2],data[3],data2[1],data2[2],data2[3],tocolor(255,0,0))
		end
	end
	]]
	-----------
	local timeNow = getTickCount()
	local progress = (((timeNow - startClientTiming)+serverTiming) / duration) % 1
	
	
	


	
		
			
	for i=1,#trainDataContainer do -- trains
		
		--[[
			Basic train data
		]]
		local trainData = trainDataContainer[i]
		
		
		
		local startLocation = trainData["startLocation"]
		
		--[[
			Find out where the train should be located
		]]
		local trainTrackIndex = trainData.currentTrainIndex or 1 
		
		local trainTravelDistance = ((trainTrackLength * progress) + startLocation) % trainTrackLength
		
		
		

		

		repeat
			trainTrackIndex = trainTrackIndex + 1
		
			local stopLoop

			if trainTrackIndex > #trainLocationTable then
				trainTrackIndex = 1
			end
			
			local trainTrackPointData = trainLocationTable[trainTrackIndex]
			
			
			local isNextPoint = trainTravelDistance < trainTrackPointData[4] -- is the travel distance greater than the next travel point?	

			if trainTrackIndex == 1 and trainData.currentTrainIndex == 1416 and trainTravelDistance < 100 then -- < this condition is used to make sure the traintrack from the START and END is correctly connected. (A bit complex)
				stopLoop = true
			elseif isNextPoint then
				stopLoop = true
				trainTrackIndex = trainTrackIndex - 1
				
				if report then report.min = report.min + 1 end
		
				if trainTrackIndex < 1 then
					trainTrackIndex = #trainLocationTable
				end
			end
			
		until stopLoop
		
		
		trainData.currentTrainIndex = trainTrackIndex
		
		
		local trainStartPoint = trainLocationTable[trainTrackIndex]
		local trainStartDistance = trainStartPoint[4]
		
		local trainTrackNexIndex = trainTrackIndex + 1
		if trainTrackNexIndex > #trainLocationTable then
			trainTrackNexIndex = 1
		end
		
		local trainEndPoint = trainLocationTable[trainTrackNexIndex]
		local trainEndDistance = trainEndPoint[4]
		
		
		local distanceBetweenPoints
		
		if trainTrackIndex == 1 then -- < this is required to make sure the transition from the START and the END of the traintrack is working correctly
			distanceBetweenPoints = trainEndDistance
			trainStartDistance = 0
		else
			distanceBetweenPoints = trainEndDistance - trainStartDistance
		end
		
		
		local distanceTraveledBetweenPoints = trainTravelDistance - trainStartDistance
		
		local trainMovingProgress = distanceTraveledBetweenPoints / distanceBetweenPoints
		
		local trainPositionX, trainPositionY, trainPositionZ = interpolateBetween (trainStartPoint[1],trainStartPoint[2],trainStartPoint[3],trainEndPoint[1],trainEndPoint[2],trainEndPoint[3],trainMovingProgress,"Linear")

		--

		
		local train = trainData["train"]

		if not train then
			createTrain(i, trainPositionX,trainPositionY,trainPositionZ)
		else
			local resetTrain = false
			
			
			if true then -- < set this condition to validate every 500 ms if each train is placed correctly. It will automatic reset the train. But with a cost of performance if the reset isn't successful.
				local checkRailRoad = timeNow > nextTrainCorrectionCheck
				if checkRailRoad then
					nextTrainCorrectionCheck = timeNow + 500
					local lastTrainPositionX, lastTrainPositionY, lastTrainPositionZ = getElementPosition(train)
					
					if getDistanceBetweenPointAndSegment3D(lastTrainPositionX, lastTrainPositionY, lastTrainPositionZ, trainStartPoint[1],trainStartPoint[2],trainStartPoint[3],trainEndPoint[1],trainEndPoint[2],trainEndPoint[3]) > 3 then
						-- Train isn't placed correctly
						resetTrain = true
						destroyTrain(i)
					end
					
				end	

			end
			
			if not resetTrain then
				local isTrainStreamedIn = isElementStreamedIn(train) 
				
				local trainCarts = trainData["trainCarts"]
				for cartIndex=#trainCarts,1, -1 do
					local trainCart = trainCarts[cartIndex]
					
					if isTrainStreamedIn then -- only attach carts if the train is streamed in.
						setTrainDerailed ( trainCart, false )
						local cartIndexAttachTo = cartIndex - 1
						local partX1, partY1, partZ1 = getElementPosition(trainCart)
						if cartIndexAttachTo == 0 then
							local partX2, partY2, partZ2 = getElementPosition(train)
							
							local distance = getDistanceBetweenPoints3D(partX1, partY1, partZ1, partX2, partY2, partZ2)
							if distance < 1 or distance > 30 then
								setTrainDerailed ( trainCart, true )
								detachTrailerFromVehicle ( train, trainCart )
								attachTrailerToVehicle ( train, trainCart )
								
								setTrainDerailed ( trainCart, false )
							end
						else
							local previousCart = trainCarts[cartIndexAttachTo]
							local partX2, partY2, partZ2 = getElementPosition(previousCart)
							
							local distance = getDistanceBetweenPoints3D(partX1, partY1, partZ1, partX2, partY2, partZ2)
							if distance < 1 or distance > 30 then
								setTrainDerailed ( trainCart, true )
								detachTrailerFromVehicle ( previousCart, trainCart )
								attachTrailerToVehicle ( previousCart, trainCart )
								
								setTrainDerailed ( trainCart, false )
							end
						end
					else
						setTrainDerailed ( trainCart, true )
						local cartIndexAttachTo = cartIndex - 1
						if cartIndexAttachTo == 0 then
							detachTrailerFromVehicle ( train, trainCart )
						else
							detachTrailerFromVehicle ( trainCarts[cartIndexAttachTo], trainCart )
						end
						setElementPosition(trainCart, 0, 0, 1000)
					end
				end
				
				--[[
					The function setTrainPosition could be used for the future, but it currently can't be trusted as it places a train an a `random` train track.
					setTrainPosition ( train, ((18107 * progress) + startLocation)  % 18107 )
					Before this can be used, this has to be implemented first: https://wiki.multitheftauto.com/wiki/SetTrainTrack
				]]
				
				setElementPosition(train, trainPositionX, trainPositionY, trainPositionZ, false)
				
			end	
		end
	end
	
	-- Automatic adjusting the dimension, to the current one.
	local localPlayerDimension = getElementDimension(localPlayer)
	if localPlayerDimension ~= getElementDimension(resourceRoot) then
		setElementDimension(resourceRoot, localPlayerDimension)
	end
end


